// Accueil.js

import axios from 'axios';
import React, { useState } from 'react';

const Accueil = () => {
    const [formData, setFormData] = useState({
        user: 'utilisateur',
        room: 'salle',
        time: 'temps',
        subject: 'sujet'
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await axios.post('http://localhost:8000/api/create_reservation/', formData);
            console.log('Réservation créée avec succès !');
        } catch (error) {
            console.error('Erreur lors de la création de la réservation :', error);
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    return (
        <div>
            <h1>Accueil</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="user" value={formData.user} onChange={handleChange} />
                <input type="text" name="room" value={formData.room} onChange={handleChange} />
                <input type="text" name="time" value={formData.time} onChange={handleChange} />
                <input type="text" name="subject" value={formData.subject} onChange={handleChange} />
                <button type="submit">Réserver</button>
            </form>
        </div>
    );
};

export default Accueil;
